import React, { useRef, useEffect, useState } from 'react';

const SpectrumGraph = ({ signals, noiseData }) => {
  const svgRef = useRef(null);
  const [dimensions, setDimensions] = useState({ width: 800, height: 400 });
  const [hoverPoint, setHoverPoint] = useState(null);

  useEffect(() => {
    const handleResize = () => {
      if (svgRef.current?.parentElement) {
        setDimensions({
          width: svgRef.current.parentElement.clientWidth,
          height: 400
        });
      }
    };

    handleResize();
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Calcular rango total del espectro basado en todas las señales
  const getFrequencyRange = () => {
    if (signals.length === 0) return { minFreq: 0, maxFreq: 2000 };

    const freqs = signals.flatMap(s => [s.fc - s.bw / 2, s.fc + s.bw / 2]);
    return {
      minFreq: Math.min(...freqs),
      maxFreq: Math.max(...freqs)
    };
  };

  const { minFreq, maxFreq } = getFrequencyRange();

  const freqToX = (freq) =>
    ((freq - minFreq) / (maxFreq - minFreq)) * dimensions.width;

  const dbmToY = (dbm) =>
    dimensions.height - 50 - (dbm * 3); // Ajuste visual

  const calculateSignalPoints = (signal) => {
    const points = [];
    const startFreq = signal.fc - signal.bw / 2;
    const endFreq = signal.fc + signal.bw / 2;

    for (let i = 0; i <= 100; i++) {
      const freq = startFreq + (i / 100) * (endFreq - startFreq);
      const x = freqToX(freq);
      const center = signal.fc;
      const spread = signal.bw / 4;
      const y =
        dbmToY(
          signal.power *
          Math.exp(-Math.pow((freq - center) / spread, 2)) // curva tipo gaussiana
        );

      points.push(`${x},${y}`);
    }

    return points.join(' ');
  };

  const renderSignals = () => {
    return signals.map((signal, index) => {
      const peakX = freqToX(signal.fc);
      const peakY = dbmToY(signal.power);

      return (
        <g key={`signal-${index}`} className="signal-group">
          <path
            d={`M${calculateSignalPoints(signal)}`}
            fill="none"
            stroke={`hsl(${index * 90}, 80%, 50%)`}
            strokeWidth="2"
            strokeOpacity="0.8"
          />
          <text
            x={peakX}
            y={peakY - 10}
            textAnchor="middle"
            fill="#333"
            fontWeight="bold"
            fontSize="12"
          >
            {signal.power} dBm
          </text>
          <text
            x={peakX}
            y={dimensions.height - 10}
            textAnchor="middle"
            fill="#555"
            fontSize="11"
          >
            {signal.name}
          </text>
        </g>
      );
    });
  };

  const renderNoiseFloor = () => {
    if (!noiseData) return null;

    const noiseY = dbmToY(noiseData.totalNoise);
    const points = [];

    for (let i = 0; i <= 100; i++) {
      const x = (i / 100) * dimensions.width;
      const y = noiseY + (Math.random() * 6 - 3); // leve variación
      points.push(`${x},${y}`);
    }

    return (
      <>
        <path
          d={`M${points.join(' ')}`}
          fill="none"
          stroke="#999"
          strokeWidth="1.5"
          strokeOpacity="0.7"
          strokeDasharray="3,2"
        />
        <text
          x={10}
          y={noiseY - 5}
          fill="#999"
          fontSize="12"
        >
          Ruido: {noiseData.totalNoise} dBm
        </text>
      </>
    );
  };

  const renderAxes = () => {
    return (
      <>
        {/* Eje X */}
        <line
          x1="0"
          y1={dimensions.height - 50}
          x2={dimensions.width}
          y2={dimensions.height - 50}
          stroke="#333"
          strokeWidth="1"
        />
        {/* Eje Y */}
        <line
          x1="50"
          y1="0"
          x2="50"
          y2={dimensions.height - 50}
          stroke="#333"
          strokeWidth="1"
        />
        {/* Etiquetas de frecuencia mínima y máxima */}
        <text x="0" y={dimensions.height - 30} fontSize="11" fill="#444">
          {minFreq.toFixed(0)} Hz
        </text>
        <text x={dimensions.width - 40} y={dimensions.height - 30} fontSize="11" fill="#444">
          {maxFreq.toFixed(0)} Hz
        </text>
      </>
    );
  };

  const handleMouseMove = (e) => {
    if (!svgRef.current) return;

    const rect = svgRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    const freq = minFreq + (x / dimensions.width) * (maxFreq - minFreq);
    setHoverPoint({ x, y, freq: Math.round(freq) });
  };

  return (
    <div className="bg-white rounded-lg shadow-xl p-4">
      <h2 className="text-xl font-semibold mb-4 text-gray-800">Visualización del Espectro</h2>
      <div
        className="relative w-full h-80 bg-gradient-to-b from-gray-50 to-white rounded overflow-hidden"
        onMouseMove={handleMouseMove}
        onMouseLeave={() => setHoverPoint(null)}
      >
        <svg
          ref={svgRef}
          width={dimensions.width}
          height={dimensions.height}
          viewBox={`0 0 ${dimensions.width} ${dimensions.height}`}
          preserveAspectRatio="none"
          className="w-full h-full"
        >
          {renderAxes()}
          {renderNoiseFloor()}
          {renderSignals()}

          {hoverPoint && (
            <>
              <line
                x1={hoverPoint.x}
                y1="0"
                x2={hoverPoint.x}
                y2={dimensions.height - 50}
                stroke="#888"
                strokeWidth="1"
                strokeDasharray="4,2"
              />
              <circle
                cx={hoverPoint.x}
                cy={hoverPoint.y}
                r="4"
                fill="#ff4757"
              />
              <text
                x={hoverPoint.x + 10}
                y={20}
                fill="#333"
                fontWeight="bold"
              >
                {hoverPoint.freq} Hz
              </text>
            </>
          )}
        </svg>
      </div>
    </div>
  );
};

export default SpectrumGraph;
