import React, { useState } from 'react';

const SignalInputForm = ({ onAddSignal }) => {
  const [signal, setSignal] = useState({
    power: '',
    bw: '',
    fc: '',
    name: ''
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    onAddSignal({
      ...signal,
      power: parseFloat(signal.power),
      bw: parseFloat(signal.bw),
      fc: parseFloat(signal.fc)
    });
    setSignal({ power: '', bw: '', fc: '', name: '' });
  };

  return (
    <div className="bg-white rounded-lg shadow-md p-4 mb-4">
      <h2 className="text-lg font-semibold mb-3 text-gray-800">Agregar Señal</h2>
      <form onSubmit={handleSubmit} className="space-y-3">
        <div>
          <label className="block text-sm font-medium text-gray-700">Nombre</label>
          <input
            type="text"
            value={signal.name}
            onChange={(e) => setSignal({...signal, name: e.target.value})}
            className="w-full mt-1 px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          />
        </div>
        <div>
          <label className="block text-sm font-medium text-gray-700">Potencia (dBm)</label>
          <input
            type="number"
            step="0.01"
            value={signal.power}
            onChange={(e) => setSignal({...signal, power: e.target.value})}
            className="w-full mt-1 px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          />
        </div>
        <div>
          <label className="block text-sm font-medium text-gray-700">Ancho de Banda (Hz)</label>
          <input
            type="number"
            value={signal.bw}
            onChange={(e) => setSignal({...signal, bw: e.target.value})}
            className="w-full mt-1 px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          />
        </div>
        <div>
          <label className="block text-sm font-medium text-gray-700">Frecuencia Central (Hz)</label>
          <input
            type="number"
            value={signal.fc}
            onChange={(e) => setSignal({...signal, fc: e.target.value})}
            className="w-full mt-1 px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          />
        </div>
        <button
          type="submit"
          className="w-full bg-blue-600 hover:bg-blue-700 text-white py-2 px-4 rounded-md transition-colors"
        >
          Agregar Señal
        </button>
      </form>
    </div>
  );
};

export default SignalInputForm;
